import React, { Component } from "react";
import Card from "./components/Card";
import Navbar from "./components/Navbar";
import Wrapper from "./components/Wrapper";
import origCards from "./cards.json";

function shuffleArray(array) {
  var currentIndex = array.length,
    temporaryValue,
    randomIndex;

  // While there remain elements to shuffle...
  while (0 !== currentIndex) {
    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    // And swap it with the current element.
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: origCards,
      topScore: 0,
      curScore: 0,
      oops: ""
    };
  }

  handleClick = id => {
    // shuffledCards is a copy of the current cards state but shuffled
    let shuffledCards = shuffleArray([...this.state.cards]);
    let chosenCard = shuffledCards.find(element => element.id === id);
    // check if chosen card has been clicked before
    if (!chosenCard.clicked) {
      // if the card has been not yet been clicked, increment the score and mark the card as clicked
      chosenCard.clicked = true;
      let newCurScore = this.state.curScore + 1;
      let newTopScore =
        newCurScore > this.state.topScore ? newCurScore : this.state.topScore;
      this.setState({
        cards: shuffledCards,
        curScore: newCurScore,
        topScore: newTopScore,
        oops: ""
      });
    } else {
      // if the card has been clicked, reset the game
      shuffledCards.forEach(card => {
        card.clicked = false;
      });
      this.setState({
        cards: shuffledCards,
        curScore: 0,
        oops: "Oops! Try again."
      });
    }
  };

  render() {
    return (
      <div className="container">
        <Navbar
          title="Clicky Game"
          curScore={this.state.curScore}
          topScore={this.state.topScore}
        />
        <Wrapper className="card-columns">
          {this.state.cards.map(element => {
            return (
              <Card
                {...element}
                key={element.id}
                handleClick={this.handleClick}
              />
            );
          })}
        </Wrapper>
        <footer className="text-center">
          Click on each box only once!
          <h3>{this.state.oops}</h3>
        </footer>
      </div>
    );
  }
}

export default App;
